import React, {useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
const [movies,setMovies]=useState([])

  function fetchMovieHandler(){
    fetch('https://swapi.dev/api/films').then(response=>{ return response.json()}).then(data=>setMovies(data.results)).catch()
  }

  return (
    <React.Fragment>
      <section>
        <button>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
